package leetcode.dynamic;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Map;
import java.util.stream.Collectors;

public class LongestSubstring {
    public int longestSubstring(String s, int k) {
        char[] chars = s.toCharArray();
        Map<Character, Long> map = group(chars);
        return longest(chars, 0, chars.length - 1, k, map);

    }

    private Map<Character, Long> group(char[] chars) {
        List<Character> charsList = new ArrayList<>();
        for (char c : chars) {
            charsList.add(c);
        }
        return charsList.stream().collect(Collectors.groupingBy(c -> c, Collectors.counting()));

    }

    private int longest(char[] chars, int start, int end, int k, Map<Character, Long> map) {
        while (start < end && map.get(chars[start]) < k) {
            start++;
        }

        while (start < end && map.get(chars[end]) < k) {
            end--;
        }

        if (end - start + 1 >= k) {
            for (int i = start; i <= end; i++) {
                if (map.get(chars[i]) < k) {
                    int ans1 = longest(chars, start, i - 1, k, group(Arrays.copyOfRange(chars, start, i)));
                    int ans2 = longest(chars, i + 1, end, k, group(Arrays.copyOfRange(chars, i + 1, end + 1)));
                    return Math.max(ans1, ans2);
                }
            }
            return end - start + 1;
        }
        return 0;
    }

    public static void main(String[] args) {
        LongestSubstring longestSubstring = new LongestSubstring();
        // System.out.println(longestSubstring.longestSubstring("aaabb", 3));
        // System.out.println(longestSubstring.longestSubstring("ababbc", 2));
        System.out.println(longestSubstring.longestSubstring("bbaaacbd", 3));

        System.out.println(
            longestSubstring.longestSubstring("zzzzzzzzzzaaaaaaaaabbbbbbbbhbhbhbhbhbhbhicbcbcibcbccccccccccbbbbbbbbaaaaaaaaafffaahhhhhiaahiiiiiiiiifeeeeeeeeee"
                , 10));
        System.out.println(longestSubstring.longestSubstring(
            "",
            301));
    }
}
